19 void UCalendarItemBase::HandleCalendarItemClick()
20 {
21     if (Sunday)
22     {
23         Sunday->OnClicked.AddDynamic(this, &UCalendarItemBase::HandleSundayClick);
24     }
25     if (Monday)
26     {
27         Monday->OnClicked.AddDynamic(this, &UCalendarItemBase::HandleMondayClick);
28     }
29     if (Tuesday)
30     {
31         Tuesday->OnClicked.AddDynamic(this, &UCalendarItemBase::HandleTuesdayClick);
32     }
33     if (Wednesday)
34     {
35         Wednesday->OnClicked.AddDynamic(this, &UCalendarItemBase::HandleWednesdayClick);
36     }
37     if (Thursday)
38     {
39         Thursday->OnClicked.AddDynamic(this, &UCalendarItemBase::HandleThursdayClick);
40     }
41     if (Friday)
42     {
43         Friday->OnClicked.AddDynamic(this, &UCalendarItemBase::HandleFridayClick);
44     }
45     if (Saturday)
46     {
47         Saturday->OnClicked.AddDynamic(this, &UCalendarItemBase::HandleSaturdayClick);
48     }
49 }
