56 void UCalendarItemBase::HandleSundayClick()
57 {
58     DaySelected = day_0;
59     ClickedTime = Owner->GetTime();
60 
61     FString DayText = DaySelected->GetText().ToString();
62     int32 DayValue = FCString::Atoi(*DayText);
63 
64     ClickedTime = FDateTime(ClickedTime.GetYear(), ClickedTime.GetMonth(), DayValue, ClickedTime.GetHour(), ClickedTime.GetMinute(), ClickedTime.GetSecond());
65 
66     OnDaySelected();
67 }
