243 void UCalendarItemBase::OnItemAdded(UObject* ListItemObjectRef)
244 {
245     if (ListItemObjectRef)
246     {
247         UCalendarRow* CalendarRowInstance = Cast<UCalendarRow>(ListItemObjectRef);
248         if (CalendarRowInstance)
249         {
250             InitializeCalendarRow(CalendarRowInstance->structure.Row,
251                 CalendarRowInstance->structure.Year,
252                 CalendarRowInstance->structure.Month,
253                 CalendarRowInstance->structure.SelectedGridColor,
254                 CalendarRowInstance->structure.EmptyGridColor,
255                 CalendarRowInstance->structure.GridColor,
256                 CalendarRowInstance->Owner);
257         }
258     }
259 }
260 
272 void UCalendarItemBase::InitializeCalendarRow(int32 RowIndex, int32 year, int32 month, FLinearColor SelectedGridColor, FLinearColor EmptyGridColor, FLinearColor GridColor, UCalendarUIBase* InOwner)
273 {
274     EnableGrids();
275     CalendarRow = RowIndex;
276     sgc_color = SelectedGridColor;
277     d_color = EmptyGridColor;
278     g_color = GridColor;
279     Year = year;
280     Month = month;
281     int32 Day = FDateTime::Now().GetDay();
282     CurrentDateTime = FDateTime(Year, Month, Day);
283     RowTimeHelper(RowIndex);
284     Owner = InOwner;
285 }
286 
292 void UCalendarItemBase::RowTimeHelper(int32 RowIndex)
293 {
294     FDateTime FirstDayOfMonth(Year, Month, 1);
295     FirstDayOfWeek = static_cast<int32>(FirstDayOfMonth.GetDayOfWeek());
296     if (FirstDayOfWeek == 6)
297     {
298         FirstDayOfWeek = 0;
299     }
300     else
301     {
302         FirstDayOfWeek += 1;
303     }
304     Offset = CalculateOffset(RowIndex);
305     int32 Count = 1;
306     for (int32 DayIndex = 0; DayIndex <= 6; DayIndex++)
307     {
308         if (!((RowIndex == 0) && (FirstDayOfWeek > DayIndex)))
309         {
310             SetDayText(DayIndex, Count);
311         }
312         else
313         {
314             SetCalendarItemHelper(DayIndex, FText::FromString(TEXT("")));
315         }
316     }
317 
318     DisableGrids();
319 }
320 
327 int32 UCalendarItemBase::CalculateOffset(int32 RowIndex)
328 {
329     int32 CalculatedOffset = 0;
330     if (RowIndex != 0)
331     {
332         CalculatedOffset = RowIndex * 7 - FirstDayOfWeek;
333     }
334     return CalculatedOffset;
335 }
336 
343 void UCalendarItemBase::SetDayText(int32 DayIndex, int32& Count)
344 {
345     bool IsWithinMonth = (Offset + Count) <= CurrentDateTime.DaysInMonth(Year, Month);
346     int32 Date = Offset + Count;
347 
348     if (IsWithinMonth && !(Date == 0))
349     {
350         SetCalendarItemHelper(DayIndex, FText::AsNumber(Date));
351     }
352     else
353     {
354         SetCalendarItemHelper(DayIndex, FText::FromString(TEXT("")));
355     }
356     Count += 1;
357 }
358 
